mapper.select:
 	 SELECT SUM(goods_price*goods_num) AS goods_price,COUNT(1) AS cart_num,SUM(goods_num) as goods_num
	 FROM sy_cart
	 WHERE buyer_id = #{buyerId}

	 	AND checked = #{checked}

 	 SELECT COUNT(1) AS num
	 FROM sy_cart
	 WHERE buyer_id = #{buyerId}

	 	AND checked = #{checked}

mapper.update:
update sy_cart
set 
  goods_num = ifnull(goods_num,0) + #{goodsNum,jdbcType=SMALLINT},
  new_add_state = #{newAddState,jdbcType=TINYINT}
where cart_id = #{cartId,jdbcType=INTEGER}
 and buyer_id= #{buyerId,jdbcType=INTEGER}
