mapper.select:
  	SELECT s.member_id,a.voucher_id ,a.voucher_code,a.voucher_price,a.voucher_t_id,a.voucher_limit,b.voucher_t_title,b.voucher_t_desc,
  	a.voucher_start_date,a.voucher_end_date,
  	b.voucher_t_activity_id,b.voucher_t_cost_mode,b.voucher_t_store_id,b.voucher_t_creator_type
	FROM  sy_voucher a LEFT JOIN sy_voucher_template b ON a.voucher_t_id=b.voucher_t_id
	LEFT JOIN sy_store s ON s.store_id=b.voucher_t_store_id
	WHERE 
	 a.voucher_state=#{voucherState}
	AND a.voucher_owner_id=#{voucherOwnerId}
	AND a.voucher_start_date < #{voucherStartDate}
	AND a.voucher_end_date > #{voucherEndDate}
	ORDER BY a.voucher_end_date ,a.voucher_active_date
