mapper.select:
SELECT GC_ID,GC_NAME,GC_SORT,GC_IMAGE,GC_PARENT_ID FROM SY_GOODS_CLASS 
WHERE SHOW_STATE='1' AND MODULE=#{module,jdbcType=INTEGER} 
ORDER BY GC_SORT

SELECT gc_id,gc_name,gc_parent_id, gc_image FROM sy_goods_class 
WHERE gc_parent_id=#{gcParentId,jdbcType=INTEGER} 
ORDER BY GC_SORT

SELECT gc_id,gc_name,gc_parent_id, gc_image FROM sy_goods_class WHERE gc_parent_id IN
#{item}
ORDER BY GC_SORT

    SELECT gc_id, gc_name, gc_sort, gc_parent_id,gc_image FROM sy_goods_class 
	WHERE gc_parent_id IN (SELECT gc_id FROM sy_goods_class WHERE gc_parent_id=#{moduleClassId,jdbcType=INTEGER}) 
	or 
	gc_parent_id=#{moduleClassId,jdbcType=INTEGER} 
	or  
	gc_id=#{moduleClassId,jdbcType=INTEGER}
