select
,
from sy_goods_common where goods_commonid = #{goodsCommonid,jdbcType=INTEGER}

  		SELECT DISTINCT a.goods_commonid,m_fee_rate,goods_price*(1-(m_fee_rate/1000)) AS poor_get_fee,transport_id,goods_name,goods_image,sale_num,goods_project_type,goods_freight,goods_price,store_name,unit,unit_spec,store_id,owner_type,poor_mid,poor_type,publisher_id,publisher_truename,product_spec,gc_id,gc_name,spec_name,goods_body,goods_type
  		,IFNULL((SELECT sprice FROM sy_transport_extend WHERE is_default=1 AND transport_id=a.transport_id),0)AS shipping_fee
		FROM sy_goods_common a INNER JOIN sy_goods_cert b ON a.goods_commonid=b.goods_commonid
