			AND a.areaid_4 = #{areaId4}


			AND a.areaid_5 = #{areaId5}


			AND a.assist_id = #{assistId}


			AND a.gc_id = #{gcId}


			AND a.goods_type IN

	            #{item}



			AND b.goods_cert_type IN

	            #{item}



			AND a.goods_project_type IN

	            #{item}



