SELECT t.* ,IFNULL(c.sprice ,0) AS shipping_fee FROM ( SELECT a.poor_mid, a.poor_type, a.publisher_id, a.product_spec, a.gc_name, a.goods_body, a.goods_type, a.owner_type, a.m_fee_rate, a.gc_id, a.goods_edittime, a.transport_id, a.publisher_truename, a.sale_num, a.goods_commonid, a.goods_addtime, a.goods_name, a.goods_project_type, a.goods_freight, a.goods_image, a.goods_price, a.store_name, a.unit, a.unit_spec, a.store_id, a.spec_name, b.boc_points_price, b.goods_storage, b.goods_marketprice, b.discount_money, (CASE WHEN a.goods_price>0 THEN b.discount_money/b.goods_price ELSE 0 END) AS discount_rate FROM sy_goods_common a INNER JOIN sy_goods b ON a.goods_commonid = b.goods_commonid WHERE a.goods_state=1
AND b.goods_price > 0
AND a.goods_project_type=#{goodsProjectType}
AND (a.goods_name LIKE #{searchQuery} OR a.gc_name LIKE #{searchQuery} OR a.publisher_truename LIKE #{searchQuery} )
AND a.gc_id = #{gcId}
AND a.publisher_id = #{publisherId}
AND b.goods_storage > 0
AND a.owner_type = #{ownerType}
AND a.areaId_1 = #{areaId1}
AND a.areaId_2 = #{areaId2}
AND a.areaid_3 = #{areaId3}
AND a.areaid_4 = #{areaId4}
AND a.areaid_5 = #{areaId5}
AND a.assist_id = #{assistId}
AND a.goods_type IN
#{item}
AND a.goods_project_type IN
#{item}
AND a.support_county_id IN
#{item}
GROUP BY a.goods_commonid
ORDER BY ${sortOrder}
LIMIT ${page.startNo},${page.pageSize}
) t LEFT JOIN `sy_transport_extend` c ON t.transport_id = c.transport_id AND c.is_default=1

SELECT t.* ,IFNULL(c.sprice ,0) AS shipping_fee FROM ( SELECT a.goods_commonid, a.goods_name, a.goods_image, a.goods_price, a.store_id, a.goods_project_type, b.boc_points_price, b.goods_storage, b.goods_marketprice, b.discount_money, a.transport_id, (CASE WHEN a.goods_price>0 THEN b.discount_money/a.goods_price ELSE 0 END) AS discount_rate FROM sy_goods_common a,sy_goods b WHERE a.goods_state=1 AND a.goods_commonid=b.goods_commonid AND b.goods_storage>0 AND a.goods_project_type=24
AND a.goods_price=#{goodsPrice}
AND a.goods_price>0
AND a.goods_commonid NOT IN
#{item.goodsCommonid}
group by a.goods_commonid
ORDER BY ${orderByStr}
LIMIT #{limit}
) t LEFT JOIN `sy_transport_extend` c ON t.transport_id = c.transport_id AND c.is_default=1

select count(goods_commonid) as num,publisher_id from sy_goods_common
group by publisher_id having num > 0
order by ${orderByClause}

