SELECT t.* ,IFNULL(c.sprice ,0) AS shipping_fee FROM ( SELECT a.gc_id, a.sale_num, a.goods_commonid, a.goods_project_type, a.goods_addtime, a.goods_name, a.goods_freight, a.goods_image, a.goods_price, a.publisher_id, a.unit, a.unit_spec, a.store_id, a.transport_id, a.spec_name, b.goods_spec FROM sy_goods_common a INNER JOIN sy_goods b ON a.goods_commonid = b.goods_commonid WHERE a.goods_state=1 AND b.show_state = '10'
AND a.assist_id > 0
AND a.goods_project_type=#{goodsProjectType}
AND a.gc_id IN
#{gcIds}
AND (a.goods_name LIKE #{searchQuery} OR a.gc_name LIKE #{searchQuery} OR a.publisher_truename LIKE #{searchQuery} )
GROUP BY a.goods_commonid
ORDER BY ${sortOrder}
LIMIT ${page.startNo},${page.pageSize}
) t LEFT JOIN `sy_transport_extend` c ON t.transport_id = c.transport_id AND c.is_default=1

  SELECT gc.gc_id  FROM sy_goods g, sy_goods_common gc
	WHERE  
	gc.goods_commonid = g.goods_commonid
	AND  gc.assist_id>0
	AND gc.goods_state  = #{goodsState}
	AND gc.goods_project_type = #{goodsProjectType}
	AND g.goods_storage > 0
	and gc.gc_id IN

		  #{item}

	GROUP BY gc.gc_id HAVING COUNT(1)>=1

SELECT t.* ,IFNULL(c.sprice ,0)AS sprice,c.id as t_id FROM (SELECT a.goods_commonid, b.goods_storage, a.areaid_1, a.areaid_2, a.areaid_3, a.transport_id, b.show_state, b.boc_points_price, (CASE WHEN a.goods_price > 0 THEN b.discount_money/b.goods_price ELSE 0 END) AS discount_rate, c.goods_cert_type FROM sy_goods_common a INNER JOIN sy_goods b ON a.goods_commonid = b.goods_commonid LEFT JOIN sy_goods_cert c ON a.goods_commonid=c.goods_commonid WHERE 1=1
AND a.goods_state=#{goodsState}
AND a.goods_commonid=#{goodsCommonid}
ORDER BY a.goods_commonid,b.goods_storage DESC
LIMIT ${page.startNo},${page.pageSize}
) t LEFT JOIN `sy_transport_extend` c ON t.transport_id = c.transport_id AND c.is_default=1

  		SELECT COUNT(*)
		FROM sy_goods_common a INNER JOIN sy_goods b ON a.goods_commonid = b.goods_commonid LEFT JOIN sy_goods_cert c ON a.goods_commonid=c.goods_commonid
		WHERE 1=1

			AND a.goods_state=#{goodsState}


			AND a.goods_commonid=#{goodsCommonid}


			LIMIT ${page.startNo},${page.pageSize}
