 SELECT 
    g.goods_price,g.m_fee_rate,g.goods_price,g.owner_type,g.poor_type,g.boc_point_rate, g.goods_id, g.goods_num, 
   g.boc_points_price*g.boc_point_rate*g.goods_num boc_points_price, g.wholesale_type,
   s.pay_sn,s.goods_project_type,g.order_id,g.store_id,g.goods_name,g.goods_image,s.order_id ,s.order_sn,s.publisher_id
  FROM
	sy_order s JOIN sy_order_goods g on s.order_id=g.order_id
 WHERE s.buyer_id=#{buyerId} AND s.deleted=#{deleted}
AND  s.order_state = #{orderState}
AND  s.order_id = #{orderId}
AND  s.publisher_id != #{publisherId}

