mapper.insert:
insert into statistics_point_details_tag (member_id, union_id, union_name, 
  channel, total_point_tag, valid_point_tag, 
  total_point, valid_point, member_mobile, 
  member_truename, member_disp_name, member_name
  )
values (#{memberId,jdbcType=INTEGER}, #{unionId,jdbcType=INTEGER}, #{unionName,jdbcType=VARCHAR}, 
  #{channel,jdbcType=VARCHAR}, #{totalPointTag,jdbcType=BIGINT}, #{validPointTag,jdbcType=BIGINT}, 
  #{totalPoint,jdbcType=DECIMAL}, #{validPoint,jdbcType=DECIMAL}, #{memberMobile,jdbcType=VARCHAR}, 
  #{memberTruename,jdbcType=VARCHAR}, #{memberDispName,jdbcType=VARCHAR}, #{memberName,jdbcType=VARCHAR}
  )

mapper.select:
	select member_id,union_id,union_name,channel,total_point_tag,valid_point_tag,total_point,valid_point,member_mobile,member_truename,member_disp_name,member_name
		from statistics_point_details_tag where 1=1

        	 and union_id = #{unionId,jdbcType=INTEGER}


	        and total_point_tag = #{totalPointTag,jdbcType=BIGINT}


	        and valid_point_tag = #{validPointTag,jdbcType=BIGINT}


	        and member_mobile = #{memberMobile,jdbcType=VARCHAR}


	        and member_truename = #{memberTruename,jdbcType=VARCHAR}
