mapper.select:
SELECT COUNT(*) FROM sy_role r, sy_agent_verify av WHERE r.areaid_3 IN
#{areaId}
AND av.status = '2' AND r.member_id = av.member_id

SELECT og.goods_commonid , SUM(goods_num) AS goods_total FROM sy_order o, sy_order_goods og, sy_goods_common gc WHERE og.order_id = o.order_id AND og.goods_commonid = gc.goods_commonid AND order_state IN(15,30,40 ) AND o.goods_project_type = 17 AND o.assist_id = 0 AND payment_time between #{yestStart} and #{yestEnd} AND o.support_county_id IN
#{areaId3}
AND gc.goods_state = 1 AND gc.trade_mode = 'pt' GROUP BY og.goods_commonid ORDER BY goods_total DESC, goods_id DESC LIMIT 4

  		SELECT bms.circle_id, bms.circle_name, (MAX(full_total_member)-MIN(full_total_member)) AS count_num
			FROM  sy_boc_member_stats bms, sy_circle c
			WHERE bms.circle_id = c.circle_id
			AND add_time BETWEEN #{yestStartTime} AND #{yestEndTime} 
			AND bms.code = #{channel}
			AND channel = #{channel}
			AND dept_level = #{deptLevel}
			ORDER BY count_num DESC
			LIMIT 10
