mapper.select:
  	SELECT a.*,b.store_id,b.store_label,b.store_name,b.store_domain
	FROM sy_voucher_template a LEFT JOIN sy_store b ON a.voucher_t_store_id=b.store_id
	WHERE a.voucher_t_state=#{voucherTState}
	AND a.voucher_t_id=#{voucherTId}
	AND a.voucher_t_end_date > #{voucherTEnddate}

  	SELECT 

	FROM sy_voucher_template 
	WHERE voucher_t_store_id=#{voucherTStoreId}
	AND voucher_t_state=#{voucherTState}
	AND voucher_t_total > voucher_t_giveout
	AND voucher_t_end_date > #{voucherTEnddate}
